#pragma once

#include <vector>

using namespace std;

class Point {
public:
	double x;
	double y;

public:
	Point(int a = 0, int b = 0) {
		x = a;
		y = b;
	}
	Point(void) {
		x = 0;
		y = 0;
	}                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                              
};

class SimplePolygon {
public:
	bool isClockWise = false;
	// 存储了简单多边形中所有的点，默认按照逆时针顺序存储
	vector<Point> points;
	// 用于存储凸包中所含的点的下标，默认按照逆时针顺序存储
	vector<int> convexHull;

public:
	// 根据后续决定来判断是只改变标记位isClockWise来确定方向，还是更改整个数组的顺序
	void reverse();
	// 判定当前的简单多边形是否合法，即是否存在自交、重合嵌套等
	bool isLegal();
	// 初步设计用于排除输入中连续的重复点
	void makeSimple();
};